var reversa = global.get('reversa');
var radar = global.get('radar');
var auto = global.get('auto');
var motDA, motDB, motIA, motIB;
var velMax = global.get('velMax')||100;
var pwmD = velMax;
var pwmI = velMax;
var sector = global.get('sector')||5;
var motores = global.get('motores')||false;



if(reversa === true)
{
    if((sector < 4)) // Esta libre la izquierda
    {
       pwmI = velMax;
       pwmD = velMax - (Math.abs((sector - 4)*10));
    }
    else if(sector > 5) // esta libre la derecha
    {
        pwmI = velMax - (Math.abs((5 - sector)*10));
        pwmD = velMax;
    }
    else
    {
        pwmI= velMax;
        pwmD = velMax;
    }
}
else
{
   if((sector < 4)) // Esta libre la izquierda
    {
       pwmI = velMax - (Math.abs((sector - 4)*10));
       pwmD = velMax;
    }
    else if(sector > 5) // esta libre la derecha
    {
        pwmI = velMax;
        pwmD = velMax - (Math.abs((5 - sector)*10));
    }
    else
    {
        pwmI= velMax;
        pwmD = velMax;
    } 
}

if((reversa === true) && ((auto === true) || motores === true)) // No hay reversa
{
    motDA = {payload:0};
    motDB = {payload:pwmD};
    motIA = {payload:0};
    motIB = {payload:pwmI};
}
else if((reversa === false) && ((auto === true) || (motores === true)))
{
    motDA = {payload:pwmD};
    motDB = {payload:0};
    motIA = {payload:pwmI};
    motIB = {payload:0};
}
else
{
    motDA = {payload:0};
    motDB = {payload:0};
    motIA = {payload:0};
    motIB = {payload:0};
}

return [motIA,motIB,motDA,motDB];

